    // Generate a secure code_verifier
    const generateCodeVerifier = () => {
      const array = new Uint32Array(56);
      window.crypto.getRandomValues(array);
      return Array.from(array, (dec) => ('0' + dec.toString(16)).substr(-2)).join('');
    };

    // Generate a base64-encoded SHA256 hash for code_challenge
    const generateCodeChallenge = async (verifier) => {
      const encoder = new TextEncoder();
      const data = encoder.encode(verifier);
      const digest = await window.crypto.subtle.digest('SHA-256', data);
      return btoa(String.fromCharCode(...new Uint8Array(digest)))
        .replace(/=/g, '')
        .replace(/\+/g, '-')
        .replace(/\//g, '_');
    };

    // Create verifier and challenge
    const codeVerifier = generateCodeVerifier();
    const codeChallenge = await generateCodeChallenge(codeVerifier);

    // Pass code_verifier in state parameter (JSON Base64-encoded)
    const state = btoa(JSON.stringify({ codeVerifier })); // Encode as Base64 JSON

    // **Log the encoded state before redirecting**
    console.log('State Sent to Singpass:', state); // <-- Add this line

    // Redirect to Singpass login
